import asyncio

from app.conn import CONN, tables
from app.conn import sql
from app.conn.tables import Dictionary
from app.models.models import RowDictionary


async def save_words():
    with open('all_rus_words.txt', 'r', encoding="utf-8") as file:
        for line in file:
            word = RowDictionary(
                word=line.upper().strip(),
                length=len(line)
            )
            await sql.to_table(Dictionary, **word.model_dump(exclude_none=True))


tables.Base.metadata.create_all(CONN.engine)
asyncio.run(save_words())